import { SET_AUTHORISED_USER, UNSET_AUTHORISED_USER } from '../actions/authedUser'

export default function authedUser(state=null, action){
    switch (action.type){
        case SET_AUTHORISED_USER:
            return action.id;
        case UNSET_AUTHORISED_USER :
            return null;
          default :
            return state
    }
}
