import { SET_AUTHED_USER, UNSET_AUTHED_USER } from '../actions/authedUser'

export default function authedUser(state=null, action){
    switch (action.type){
        case SET_AUTHED_USER:
            return action.id;
        case UNSET_AUTHED_USER :
            return null;
          default :
            return state
    }
}
